document.write(`<html><style>button{position: relative;}</style><body id="myBody"><a href="http://bit.ly/1NMH6Jf">Show code</a><h1>Find <span id="myChallenge"></span></h1><script>function getRandm(e,n){return~~(Math.random()*(n-e+1))+e}function generateButtons(e,n){for(var t=e,a=t+n,o=doc.getElementById("myBody"),d=t;a>d;d++){var r=doc.createElement("BUTTON"),l=doc.createTextNode(String.fromCharCode(d));r.appendChild(l),o.appendChild(r)}}function placeBtns(){for(var e=doc.getElementsByTagName("button"),n=0;n<e.length;n++)e[n].style.left=getRandm(0,w)+"px",e[n].style.top=getRandm(0,h)+"px",e[n].addEventListener("click",function(e){e.target.innerText===toFind&&(placeBtns(),newChallenge())})}function newChallenge(){toFind=String.fromCharCode(getRandm(minCharCode,minCharCode+range-1)),myChallenge.innerText=toFind}var w=250,h=250,doc=document,myChallenge=doc.getElementById("myChallenge"),minCharCode=65,range=10,toFind;generateButtons(minCharCode,range),placeBtns(),newChallenge();</script></body></html>`)